function App() {
  return (
    <>
      <TodoCounter completed={16} total={20} />
      <TodoSearch />

      <TodoList>
        {defaultTodos.map(todo => (
          <TodoItem 
          key={todo.text} 
          text={todo.text}
          completed={todo.completed}
           />
        ))}
      </TodoList>

     <CreateTodoButton />
    </>
  );
}

export default App;
